function hasSpecialAccess()
	local specialAccess = {
		[1616] = "Dev",
		[4664] = "Beta",
		[10626] = "Beta",
		[11535] = "Beta",
		[33953] = "Beta",
	}
	local hasAccess = specialAccess[Cherax.GetUID()]
	if hasAccess then
		return hasAccess
	else
		return nil
	end
end

function blacklist()
	local blacklistedUsers = {}
	for _, uid in pairs(blacklistedUsers) do
		if Cherax.GetUID() == uid then
			SetShouldUnload()
			return
		end
	end
	Script.Yield(100)
end
