    function StructInstance(struct, base)
        local temp = {}
        
        for k, v in pairs(struct) do
            if type(v) == "number" then
                temp[k] = v + base
            elseif type(v) == "table" then
                temp[k] = StructInstance(v, base)
            end
        end
        
        return temp
    end
    
    function StructInstanceArray(struct, base, size, count)
        temp = {} 
        tempBase = base + 1
        
        for i = 0, count - 1 do
            temp[i] = StructInstance(struct, tempBase)
            tempBase = tempBase + size
        end
        
        return temp
    end
    
    function ValueArray(base, count)
        temp = {} 
        tempBase = base + 1
        
        for i = 0, count - 1 do
            temp[i] = tempBase + i
        end
        
        return temp
    end
    
