local featureState = {}
function FeatureMgr.AddLoop(hash, name, featureType, description, onFeatureEnable, onFeatureDisable)
    if not featureState[hash] then
        featureState[hash] = false
    end

    return FeatureMgr.AddFeature(hash, name, featureType, description, function()
        if FeatureMgr.GetFeature(hash) == 0 or FeatureMgr.GetFeature(hash) == nil then
            return
        end
        
        local isEnabled = FeatureMgr.GetFeatureIsEnabled(hash)
        if isEnabled then
            featureState[hash] = true
            Script.RegisterLooped(function()
                if ShouldUnload() or not FeatureMgr.GetFeatureIsEnabled(hash) then
                    return
                end
                onFeatureEnable()
            end)
        elseif onFeatureDisable then
            if featureState[hash] then
                onFeatureDisable()
                featureState[hash] = false
            end
        end
    end)
end

FeatureMgr.GetFeatureIsEnabled = function(hash)
	if FeatureMgr.GetFeature(hash) == nil then
		if Cherax.GetUID() == 24370 then
			Logger.Log(eLogColor.RED, "JinxScript", "Error Code: U1 | Type: EXCEPTION_ACCESS_VIOLATION")
			GUI.AddToast("JinxScript", "Caught an exception, see console for details.", 7000)
			SetShouldUnload()
		end
		return
	end
	return FeatureMgr.IsFeatureEnabled(hash)
end


FeatureMgr.AddPlayerLoop = function(hash, name, fType, desc, callback)
    local features = FeatureMgr.AddPlayerFeature(hash, name, fType, desc, callback)

    for k, v in pairs(features) do
        FeatureMgr.GetFeature(v):RegisterCallbackTrigger(eCallbackTrigger.OnTick)
    end
end
