function isNetPlayerOk(playerID, assert_playing, assert_done_transition)
	if not NETWORK.NETWORK_IS_PLAYER_ACTIVE(playerID) then return false end
	if assert_playing and not PLAYER.IS_PLAYER_PLAYING(playerID) then return false end
	if assert_done_transition then
		if playerID == ScriptGlobal.GetInt(MPGlobals.g_iLocalPlayerID) then
			return ScriptGlobal.GetInt(MPGlobals.g_bLocalPlayerActive) ~= 0
		elseif ScriptGlobal.GetInt(GlobalplayerBD[playerID].iGameState) ~= 4 then
			return false
		end
	end
	return true
end

function isMainGameStateRunning(playerID)
	return ScriptGlobal.GetInt(GlobalplayerBD[playerID].iGameState) == 4
end

function getAllPeds()
	local pedCount = PoolMgr.GetMaxPedCount() - 1
	local peds = {}
	for i = 0, pedCount do
		local ped = PoolMgr.GetPed(i)
		if ped ~= nil then
			table.insert(peds, ped)
		end
	end
	return peds
end

cachedPeds = {}
local isCacheActive = false
local maxCachedPeds = 256 
function updateCachedPeds()
    if isCacheActive then
        return
    end
    isCacheActive = true

    local peds = getAllPeds()
    local cachedPedsCount = 0

    if #cachedPeds > maxCachedPeds then
        cachedPeds = {}
    end

    for _, ped in pairs(peds) do
        if ped ~= getPlayerPed() then
            cachedPedsCount = cachedPedsCount + 1
            cachedPeds[cachedPedsCount] = ped
        end
    end

    Script.Yield(500)
    isCacheActive = false
end

