function startScript(script, stack_size)
	local scriptID
	if not SCRIPT.DOES_SCRIPT_EXIST(script) then
		return
			false
	end

	local timeout = Time.GetEpocheMs() + 1000
	while not SCRIPT.HAS_SCRIPT_LOADED(script) and Time.GetEpocheMs() < timeout and not ShouldUnload() do
		SCRIPT.REQUEST_SCRIPT(script)
		Script.Yield()
	end

	if not SCRIPT.HAS_SCRIPT_LOADED(script) then
		return false
	end
	if stack_size <= 0 then
		stack_size = 512
	end
	scriptID = SYSTEM.START_NEW_SCRIPT(script, stack_size)
	SCRIPT.SET_SCRIPT_AS_NO_LONGER_NEEDED(script)
	return true, scriptID
end


