function redirectDamageToPlayer(event)
	if not FeatureMgr.GetFeatureIsEnabled(Utils.Joaat("RedirectWeaponDamage")) then
		return
	end
	
	if string.find(Players.GetTags(event.senderId), "[G]") then
		return
	end
	Script.QueueJob(function()
		if event.weaponHash == 0 then
			event.weaponHash = WEAPON.GET_SELECTED_PED_WEAPON(GTA.PointerToHandle(Players.GetCPed(event.senderId)))
		end
		if event.damage == 0.0 then
			event.damage = WEAPON.GET_WEAPON_DAMAGE(event.weaponHash)
			if event.meleeDamage or event.damage == 0.0 then
				return false
			end
		end
		if event.senderId == nil then
			return
		end
		local CPed = Players.GetCPed(event.senderId):GetAddress()
		local pedPtr = Players.GetCPed(GTA.GetLocalPlayerId()):GetAddress()
		if not WEAPON.IS_WEAPON_VALID(event.weaponHash) and isDebugMode then
			GUI.AddToast("Redirect Weapon Damage", "Invalid Weapon Hash", 5000)
		end
		local randomPed
		if PLAYER.IS_PLAYER_DEAD(GTA.GetLocalPlayerId()) or event.shouldKill and WEAPON.GET_WEAPONTYPE_MODEL(event.weaponHash) ~= 0 then
			Memory.LuaCallCFunction(CWeaponDamageEvent__Trigger, pedPtr, CPed, CPed + 0x90, 0, 1, event.weaponHash, event.damage, 0, 0, event.flags, 0, 0, 0, 0, 0, 0, 0, V3.New())
			local pos = NETWORK.NETWORK_GET_LAST_PLAYER_POS_RECEIVED_OVER_NETWORK(event.senderId)
			randomPed = GTA.CreateRandomPed(pos.x, pos.y, pos.z - 50)
			pedPtr = GTA.HandleToPointer(randomPed)
			Script.Yield(25)
			if GTA.HandleToPointer(randomPed) then
				pedPtr = GTA.HandleToPointer(randomPed):GetAddress()
				ENTITY.FREEZE_ENTITY_POSITION(randomPed, true)
				Script.Yield(150)
			end
		end
		if pedPtr then
			Memory.LuaCallCFunction(CWeaponDamageEvent__Trigger, pedPtr, CPed, CPed + 0x90, 0, 1, event.weaponHash, event.damage, 0, 0, event.flags, 0, 0, 0, 0, 0, 0, 0, V3.New())
			if randomPed then
				Script.Yield(500)
				deleteEntity(randomPed)
			end
		end
	end)			
end

function netEvent(sender, eventID, datBitBuffer)
	if sender then
		if eventID == 6 then
			local event = getWeaponEventData(datBitBuffer, sender)
			if event then
				if FeatureMgr.GetFeatureIsEnabled(Utils.Joaat("NotifyOnDamageEvents")) then
					Logger.LogInfo("WEAPON_DAMAGE_EVENT From " ..
					sender:GetGamerInfo().Name ..
					" | Weapon: " .. GTA.GetDisplayNameFromHash(event.weaponHash) .. " | Damage: " .. event.damage .. " | Flags: " .. event.flags)
					GUI.AddToast("Network Event", "Weapon Damage Event From " .. sender:GetGamerInfo().Name, 5000)
				end
				local isAgainstMe, reason = isDamageAgainstMe(event)
				-- if isDebugMode then
				-- Logger.LogInfo("WEAPON_DAMAGE_EVENT from " .. Players.GetName(event.senderId) .. " | Weapon Hash: " .. event.weaponHash .. " | Weapon Damage: " .. event.damage .. " | Flags: " .. event.flags)
				-- end
				if isAgainstMe then
					redirectDamageToPlayer(event)
				end
			end
		elseif eventID == 17 and FeatureMgr.GetFeatureIsEnabled(Utils.Joaat("NotifyOnExplosions")) then
			GUI.AddToast("Network Event", "Explosion From " .. sender:GetGamerInfo().Name, 5000)
		elseif eventID == 82 and FeatureMgr.GetFeatureIsEnabled(Utils.Joaat("NotifyOnReportMyself")) then
			GUI.AddToast("Network Event", "Report Myself Event From " .. sender:GetGamerInfo().Name, 5000)
			Logger.LogInfo("Report Myself Event From " .. sender:GetGamerInfo().Name)
		end
	end
	return false
end

