function onPlayerLeft(playerID)
    local PlayerName = Players.GetName(playerID)

    if infoLists.modderPids[playerID] then
        infoLists.modderNames = findAndRemove(infoLists.modderNames, PlayerName)
        infoLists.modderPids[playerID] = nil
    end

    if infoLists.friendPids[playerID] then
        infoLists.friendNames = findAndRemove(infoLists.friendNames, PlayerName)
        infoLists.friendPids[playerID] = nil
    end

    if infoLists.attackerPids[playerID] then
        infoLists.attackerNames = findAndRemove(infoLists.attackerNames, PlayerName)
        infoLists.attackerPids[playerID] = nil
    end

    if infoLists.godmodePids[playerID] then
        infoLists.godmodeNames = findAndRemove(infoLists.godmodeNames, PlayerName)
        infoLists.godmodePids[playerID] = nil
    end

    local tableStuff = {
        cachedModData = cachedModData,
        cachedVehData = cachedVehData,
        godmodeDetected = godmodeDetected,
        moddedOrbDetected = moddedOrbDetected,
        moddedDataDetected = moddedDataDetected,
        superDriveDetected = superDriveDetected,
        antiLockonDetected = antiLockonDetected,
        yimUserDetected = yimUserDetected,
        moddedUpgradeDetected = moddedUpgradeDetected,
        isPlayerModder = isPlayerModder,
        isUsingRC = isUsingRC,
        armedPlayers = armedPlayers,
        playerInVehicle = playerInVehicle,
        playerIsGodmode = playerIsGodmode,
    }

    for _, tbl in pairs(tableStuff) do
        if tbl[playerID] then
            tbl[playerID] = nil
        end
    end
end

