			infoLists.attackerPids[playerID] = true

			local eventType = reason == eProtectionType.CRASH and "Crash Event" or "Kick Event"
			table.insert(infoLists.attackerNames, string.format("%s (%s)", Players.GetName(playerID), eventType))
		end
	end
