    local vehicle = PED.GET_VEHICLE_PED_IS_USING(ped)

    if not VEHICLE.ARE_ANY_VEHICLE_SEATS_FREE(vehicle) then
        GUI.AddToast("JinxScript", "There are no available seats. :/", 5000)
        return
    end
    
    PED.SET_PED_INTO_VEHICLE(getPlayerPed(), vehicle, seat)
end


