        Script.Yield(250)
        Script.ExecuteAsScript("am_challenges", function()
            ScriptLocal.SetInt(Utils.sJoaat("am_challenges"), serverBD_Challenges.warningTimer, NETWORK.GET_NETWORK_TIME() - ScriptGlobal.GetInt(g_sMPTunables.iChallenge_Event_Start_Countdown_Time))
