function isPlayerInInterior(playerID)
	local groupID = INTERIOR.GET_INTERIOR_GROUP_ID(getPlayerCurrentInterior(playerID))
	local playerTags = Players.GetTags(playerID)
	return string.find(playerTags, "%[I%]") or
	(groupID == 0 or groupID == 119) and getPlayerCurrentInterior(playerID) ~= 0
end

