Players.GetTutorialSessionTeamID = function(playerID)
	local CPed = Players.GetCPed(playerID)
	if not CPed or CPed == 0 then
		return
	end
	local CPedAddress = CPed:GetAddress()
	local netObj = Memory.ReadLong(CPedAddress + 0xD0)
	if netObj == 0 then return end
	local iTeamID = Memory.ReadByte(netObj + 0x469)
	if netObj ~= 0 and iTeamID ~= 6 then
		return iTeamID
	end
	return 0
end

Players.GetTutorialSessionInstanceID = function(playerID)
	local CPedAddress = Players.GetCPed(playerID):GetAddress()
	local netObj = Memory.ReadLong(CPedAddress + 0xD0)
	if netObj == 0 then return end
	local iInstanceID = Memory.ReadByte(netObj + 0x46A)
	if netObj ~= 0 and iInstanceID ~= 64 then
		return iInstanceID
	end
end

