Players.KillThroughGodmode = function(playerID)
	local ped = PLAYER.GET_PLAYER_PED_SCRIPT_INDEX(playerID)
	local CPed = Players.GetCPed(playerID):GetAddress()
	local pedPtr = Players.GetCPed(GTA.GetLocalPlayerId()):GetAddress()
	local ping = math.floor(NETWORK.NETWORK_GET_AVERAGE_PING(playerID))
	local timer = (ping > 300) and (Time.GetEpocheMs() + 5000) or (Time.GetEpocheMs() + 3000)
	Script.Yield()
	Script.Yield()
	if not CPed or CPed == 0 then
		return
	end
	repeat
		GTA.TriggerScriptEvent(1 << playerID,
			{ SCRIPT_EVENT.SCRIPT_EVENT_GENERAL, GTA.GetLocalPlayerId(), 1 << playerID, SCRIPT_EVENT
				.GENERAL_EVENT_HEIST_PREPLAN_EXIT_GUEST_MODE, math.random(0, 9999) })
		Memory.LuaCallCFunction(CWeaponDamageEvent__Trigger, pedPtr, CPed, CPed + 0x90, 0, 1,
			Utils.sJoaat("weapon_snowball"), 1000.0, 0, 0,
			eDamageFlags.DF_IgnorePedFlags | eDamageFlags.DF_SuppressImpactAudio | eDamageFlags.DF_IgnoreRemoteDistCheck,
			0, 0, 0, 0, 0, 0, 0, V3.New())
		if Time.GetEpocheMs() > timer then
			GUI.AddToast("JinxScript", Players.GetName(playerID) .. "'s godmode can not be removed. :/", 5000)
			timer = Time.GetEpocheMs() + 3000
			return
		end
		Script.Yield()
	until ShouldUnload() or PED.IS_PED_DEAD_OR_DYING(ped)
	Script.Yield()
	Script.Yield()
	timer = Time.GetEpocheMs() + 3000
end
