local memoryScan = Memory.Scan
Memory.Scan = function(name, signature, ripOffset, callback)
    if type(ripOffset) == "function" then
        callback = ripOffset
        ripOffset = nil
    end

    PointerBatch.Add(name, signature, function(scan)
        local rip = ripOffset and Memory.Rip(scan + ripOffset) or scan

        if rip == 0 then
            return false
        end

        if callback then
            callback(rip)
        end
    end)
end

PointerBatch = {
    Batch = {}
}

PointerBatch.Add = function(name, signature, callback)
    table.insert(PointerBatch.Batch, { name = name, signature = signature, callback = callback })
end

PointerBatch.Run = function()
    local startTime = Time.GetEpocheMs()
    local totalSignatures = 0
    local failedPatterns = {}

    for _, batchData in ipairs(PointerBatch.Batch) do
        local scan = memoryScan(batchData.signature)

        if scan == 0 or batchData.callback(scan) == false then
            scan = nil
            table.insert(failedPatterns, batchData.name)
        else
            Logger.Log(eLogColor.CYAN, "JinxScript", "Found '" .. batchData.name .. "'")
            totalSignatures = totalSignatures + 1
        end
    end

    if #failedPatterns > 0 then
        for _, patternName in ipairs(failedPatterns) do
            Logger.Log(eLogColor.RED, "JinxScript Error", "Failed to find '" .. patternName .. "'")
        end
        --SetShouldUnload()
    end

    return totalSignatures, (Time.GetEpocheMs() - startTime - 10)
end
    

local scanScript = Memory.ScanScript
Memory.ScanScript = function(scriptName, pattern, add, callback)
    Script.QueueJob(function()
        while not GTA.IsScriptRunning(scriptName) do
            Script.Yield()
        end

        local scriptHash = Utils.Joaat(scriptName)

        local result
        if add then
            result = scanScript(scriptHash, pattern) + add
        else
            result = scanScript(scriptHash, pattern)
        end

        if callback then
            callback(result)
        end
    end)
end

