BytePatch = {}
BytePatch.__index = BytePatch
BytePatch.allPatches = {} 

setmetatable(BytePatch, {
    __call = function(cls, address, name, bytes)
        return cls:Add(address, name, bytes)
    end,
})

function BytePatch:Add(address, name, bytes)
    if address == 0 then
        return Logger.Log(eLogColor.RED, "JinxScript", "Failed To Find '" .. name .. "' [BP]")
    end

    local instance = setmetatable({ address = address, bytes = bytes, originalBytes = {}, name = name or nil }, BytePatch)
    for i = 0, #bytes - 1 do
        instance.originalBytes[i + 1] = Memory.ReadByte(address + i)
    end

    table.insert(BytePatch.allPatches, instance)
    return instance
end

function BytePatch:Apply()
    for i = 0, #self.bytes - 1 do
        Memory.WriteByte(self.address + i, self.bytes[i + 1])
    end
end

function BytePatch:Toggle(bEnable)
    if bEnable then
        self:Apply()
    else
        self:Restore()
    end
end

function BytePatch:Restore()
    for i = 0, #self.originalBytes - 1 do
        Memory.WriteByte(self.address + i, self.originalBytes[i + 1])
    end
end


function BytePatch:RestoreAll()
    Logger.Log(eLogColor.CYAN, "JinxScript", "Resetting Pointers...")
    for _, Patch in ipairs(BytePatch.allPatches) do
        Patch:Restore()
    end
end
