FeatureMgr.AddLoop(Utils.Joaat("EntityGrabber"), "Entity Grabber", eFeatureType.Toggle, "Description: Pick up vehicles and throw them.\nUsage: E on keyboard & LB on controller.", function()
    local pos = getPlayerPosition(GTA.GetLocalPlayerId())
    local closestVehicle = VEHICLE.GET_CLOSEST_VEHICLE(pos.x, pos.y, pos.z, 5.0, 0, 1 | 2 | 4 | 8 | 16 | 32 | 64 | 2048 | 32768 | 65536 | 131072 | 262144 | 524288)
    local camRot =  CAM.GET_FINAL_RENDERED_CAM_ROT(2)
    local finalRot = camRot:RotationToDirection()
    local dict = "anim@mp_rollarcoaster"
    local anim = "hands_up_idle_a_player_one"
    local vehPos = ENTITY.GET_ENTITY_COORDS(closestVehicle)
    local distanceToVehicle = SYSTEM.VDIST(pos.x, pos.y, pos.z, vehPos.x, vehPos.y, vehPos.z)
    requestAnimDict(dict)

    if closestVehicle and distanceToVehicle < 5.0 and not PED.IS_PED_IN_ANY_VEHICLE(getPlayerPed()) and PAD.IS_CONTROL_JUST_PRESSED(0, 38) then
        if includePlayerVehicles then
            if not NETWORK.NETWORK_HAS_CONTROL_OF_ENTITY(closestVehicle) then
                requestControl(closestVehicle, 2500)
                if not NETWORK.NETWORK_HAS_CONTROL_OF_ENTITY(closestVehicle) then
                    return
                end
            end
        end

        TASK.TASK_PLAY_ANIM(getPlayerPed(), dict, anim, 3.0, 3.0, -1, 1|16|32, 0.0, false, false, false)
        Script.Yield(150)
        ENTITY.ATTACH_ENTITY_TO_ENTITY(closestVehicle, getPlayerPed(), 0, 0.0, 0.0, 1.4, 0.0, 0.0, -90.0, true, false, false, false, 0, true, false)
        Script.Yield(500)

        repeat
            distanceToVehicle = SYSTEM.VDIST2(pos.x, pos.y, pos.z, vehPos.x, vehPos.y, vehPos.z)
            camRot =  CAM.GET_FINAL_RENDERED_CAM_ROT()
            finalRot = camRot:RotationToDirection()
            Script.Yield()
        until PAD.IS_CONTROL_JUST_PRESSED(0, 38) or distanceToVehicle < 5.0

        if includePlayerVehicles then
            if not NETWORK.NETWORK_HAS_CONTROL_OF_ENTITY(closestVehicle) then
                requestControl(closestVehicle, 2500)
            end
        end
        
        ENTITY.DETACH_ENTITY(closestVehicle, true, true)
        ENTITY.APPLY_FORCE_TO_ENTITY(closestVehicle, 3, finalRot.x * entityForce, finalRot.y * entityForce, finalRot.z * entityForce, V3.New(), 0, false, false, true, false, true)
        TASK.CLEAR_PED_TASKS(getPlayerPed())
    end
end)
