local cachedNoRagdoll = nil
FeatureMgr.AddLoop(Utils.Joaat("EnableNinjaJump"), "Activate Ninja Jump", eFeatureType.Toggle, "Description: Gives you the ability to do flips while super jumping.\nNote: Other players do not see this.", function()
    local isSuperJumpEnabled = FeatureMgr.GetFeatureIsEnabled(Utils.Joaat("SuperJump"))
    local isNoclipping = FeatureMgr.GetFeatureIsEnabled(Utils.Joaat("NoClip"))
	local ped = getPlayerPed()

    if not isSuperJumpEnabled then
        superJump:SetValue(true):TriggerCallback()
    end

	if cachedNoRagdoll == nil then
		cachedNoRagdoll = noRagdoll:GetBoolValue()
	end

    if PED.IS_PED_JUMPING(ped) and ENTITY.GET_ENTITY_HEIGHT_ABOVE_GROUND(ped) > 2.0 then
        noRagdoll:SetValue(false):TriggerCallback()

        local heading = ENTITY.GET_ENTITY_HEADING(ped)
        local pitch = 0
        local numOfFlips = (flips == 0) and math.huge or flips
        local countedFlips = 0

        while not ShouldUnload()
            and PED.IS_PED_JUMPING(ped)
            and countedFlips < numOfFlips
            and not isNoclipping
            and FeatureMgr.GetFeatureIsEnabled(Utils.Joaat("EnableNinjaJump")) do

            isNoclipping = FeatureMgr.GetFeatureIsEnabled(Utils.Joaat("NoClip"))
            
            pitch = pitch + 360 * SYSTEM.TIMESTEP() * flipsPerSecond

            while pitch > 360 and not ShouldUnload() do
                pitch = pitch - 360
                countedFlips = countedFlips + 1
            end

            ENTITY.SET_ENTITY_ROTATION(ped, pitch * (direction == 1 and -1 or 1), 0.0, heading, 2, true)
            Script.Yield()
        end

        if isNoclipping then
            TASK.CLEAR_PED_TASKS(ped)
        end

        if clearTasks then
            if cachedNoRagdoll == false then
                noRagdoll:SetValue(true):TriggerCallback()
            end

            WEAPON.GIVE_DELAYED_WEAPON_TO_PED(ped, Utils.sJoaat("gadget_parachute"), 1, false)
            TASK.CLEAR_PED_TASKS(ped)

            Script.Yield()
            Script.Yield()

            while not ShouldUnload() and TASK.GET_IS_TASK_ACTIVE(ped, 6) or ENTITY.IS_ENTITY_IN_AIR(ped) do
                if not ENTITY.IS_ENTITY_IN_AIR(ped) then break end
                Script.Yield()
            end

            noRagdoll:SetValue(cachedNoRagdoll):TriggerCallback()
        else
            while not ShouldUnload() and PED.IS_PED_JUMPING(ped) do
                Script.Yield()
            end 
            noRagdoll:SetValue(cachedNoRagdoll):TriggerCallback() 
        end
    end
end, function()
    local ped = getPlayerPed()
    ENTITY.SET_ENTITY_ROTATION(ped, 0.0, 0.0, ENTITY.GET_ENTITY_HEADING(ped), 2, true)
    superJump:SetValue(false):TriggerCallback()
    noRagdoll:SetValue(cachedNoRagdoll):TriggerCallback()
end)

