FeatureMgr.AddFeature(Utils.Joaat("RestartFreemode"), "Restart Freemode", eFeatureType.Button, "Description: Restarts the freemode script.", function()
    local SmoothTransition = FeatureMgr.GetFeatureByName("Smooth Transition")
    if not GTA.IsScriptRunning("freemode") then
        GUI.AddToast("JinxScript", "Freemode script is currently not running, attempting to start it...", 5000)
    end

    if NETWORK.NETWORK_GET_HOST_OF_SCRIPT("freemode", -1, 0) == GTA.GetLocalPlayerId() then
        for _, playerID in pairs(Players.Get()) do
            if playerID ~= GTA.GetLocalPlayerId() then
                GTA.GiveScriptHost(playerID, Utils.sJoaat("freemode"))
                Script.Yield(500)
                break
            end
        end
    end

    local prevPos = getPlayerPosition(GTA.GetLocalPlayerId())

    if SCRIPT.GET_NUMBER_OF_THREADS_RUNNING_THE_SCRIPT_WITH_THIS_HASH(Utils.sJoaat("MainTransition")) > 0 then
        Script.ExecuteAsScript("MainTransition", function()
            SCRIPT.TERMINATE_THIS_THREAD()
        end)
    end
    
    requestScript("MainTransition")
    
    Script.ExecuteAsScript("freemode", function()
        SCRIPT.TERMINATE_THIS_THREAD()
    end)

    while SCRIPT.GET_NUMBER_OF_THREADS_RUNNING_THE_SCRIPT_WITH_THIS_HASH(Utils.sJoaat("freemode")) > 0 and not ShouldUnload() do
        Script.Yield()
    end

    for i = 0, ScriptGlobal.GetInt(g_numGeneratedHeadshotsMP) - 1 do
        local headshot = ScriptGlobal.GetInt(g_sGeneratedHeadshotsMP[i].hsdHeadshotID)
        if PED.IS_PEDHEADSHOT_VALID(headshot) then
            PED.UNREGISTER_PEDHEADSHOT(headshot)
        end
    end

    local success, scriptID = startScript("MainTransition", 8032)
    if not success then 
        return false 
    end

    ScriptGlobal.SetInt(g_tiTransitionThread, scriptID)
    SCRIPT.SET_SCRIPT_AS_NO_LONGER_NEEDED("MainTransition")
    
    while getTransitionState() ~= 58 and not ShouldUnload() do 
        Script.Yield() 
    end

    if seamlessTransition then 
        if not SmoothTransition:GetBoolValue() then
            SmoothTransition:SetValue(true)
            bSmoothTransitionOverridden = true
        end
        ScriptGlobal.SetInt(g_Private_TransitionState, 22)
    else
        ScriptGlobal.SetInt(g_Private_TransitionState, 10)   
    end

    GRAPHICS.RESET_PAUSED_RENDERPHASES()
    GRAPHICS.ANIMPOSTFX_STOP_ALL()
    while getTransitionState() ~= 66 and not ShouldUnload() do
        if respawnAtPrevPos and not seamlessTransition then
            ENTITY.SET_ENTITY_COORDS_NO_OFFSET(getPlayerPed(), prevPos.x, prevPos.y, prevPos.z, false, false, false)
        end
        Script.Yield(100)
    end

    if bSmoothTransitionOverridden then 
        SmoothTransition:SetValue(false)
    end
end)

