    if not isNetPlayerOk(GTA.GetLocalPlayerId(), false, true) or NETWORK.NETWORK_IS_ACTIVITY_SESSION() or isPlayerInInterior(GTA.GetLocalPlayerId()) then return end
    local interior = ScriptGlobal.GetInt(GlobalplayerBD[GTA.GetLocalPlayerId()].interiorCurrent)
