        local pObject = GTA.HandleToPointer(object)
        if pObject and pObject.NetObject and pObject.NetObject.PlayerId ~= GTA.GetLocalPlayerId() then
            local pos = GTA.GetLocalPed().Position
            local objPos = pObject.Position
            local modelHash = "Unknown"
