            local vel = NETWORK.NETWORK_GET_LAST_VEL_RECEIVED_OVER_NETWORK(ped)
            local isPlayerMoving = (math.sqrt(vel.x^2 + vel.y^2 + vel.z^2)) > 1.5
            if isPlayerGodmode(playerID) and not godmodeDetected[playerID] and isPlayerMoving then
                repeat
                    vel = NETWORK.NETWORK_GET_LAST_VEL_RECEIVED_OVER_NETWORK(ped)
                    isPlayerMoving = (math.sqrt(vel.x^2 + vel.y^2 + vel.z^2)) > 1.5
                    if (not isPlayerMoving and timeSinceLastMove > 3) or playerID == nil or not isPlayerGodmode(playerID) or not isFreemodeActive(playerID) or isPlayerInInterior(playerID) or pos.z < 0.0 then
