                    timeSinceLastMove = not isPlayerMoving and timeSinceLastMove + 1 or 0
                until ShouldUnload() or Time.GetEpocheMs() > timer
                vel = NETWORK.NETWORK_GET_LAST_VEL_RECEIVED_OVER_NETWORK(ped)
                isPlayerMoving = (math.sqrt(vel.x^2 + vel.y^2 + vel.z^2)) > 1.5
                if Time.GetEpocheMs() > timer and playerID ~= nil and isFreemodeActive(playerID) and isPlayerGodmode(playerID) and not isPlayerInInterior(playerID) and isPlayerMoving then
                    ModderDB.AddModderByPlayerId(playerID, "Godmode")
                    godmodeDetected[playerID] = true
