    local timer = Time.GetEpocheMs() + 10000 -- compensating for player latency and vehicle chaff
    if NETWORK.NETWORK_IS_ACTIVITY_SESSION() then return end
    for _, playerID in pairs(Players.Get()) do
        if playerID ~= GTA.GetLocalPlayerId() then
            local ped = PLAYER.GET_PLAYER_PED_SCRIPT_INDEX(playerID)
            local vehicle = PED.GET_VEHICLE_PED_IS_USING(ped)
            local driver = NETWORK.NETWORK_GET_PLAYER_INDEX_FROM_PED(VEHICLE.GET_PED_IN_VEHICLE_SEAT(vehicle, -1))
            local bitset = DECORATOR.DECOR_GET_INT(vehicle, "MPBitset")
            local vehicleMdl = ENTITY.GET_ENTITY_MODEL(vehicle)
            local legitBitsets = {2048, 3072, 10240, 11264, 11272, 33792} -- the game sets some vehicles not targetable that are parts of fm activities
            local continue = false
            if vehicle == 0 then 
                continue = true
            end
            for _, bitsets in pairs(legitBitsets) do
                if bitset == bitsets then
                    continue = true
                end
            end

            if not PED.IS_PED_IN_ANY_VEHICLE(ped) or (not doesVehicleHaveImaniTech(vehicle) and VEHICLE.GET_VEHICLE_MOD(vehicle, 44) == 1) then 
                continue = true
            end

            if not continue then
                if not antiLockonDetected[playerID] then
                    local pVehicle = GTA.HandleToPointer(vehicle):GetAddress()
                    if Memory.ReadByte(pVehicle + 0xAEE) == 0 and playerID == driver then
                        local bAllowHomingMissileLockon = Memory.ReadByte(pVehicle + 0xAEE)
                        repeat
                            if bAllowHomingMissileLockon == 1 then
                                timer = Time.GetEpocheMs() + 10000
                                return
                            end
                            Script.Yield()
                        until ShouldUnload() or Time.GetEpocheMs() > timer
                        if bAllowHomingMissileLockon == 0 then
                            ModderDB.AddModderByPlayerId(playerID, "Anti-Lockon")
                            antiLockonDetected[playerID] = true
                            timer = Time.GetEpocheMs() + 10000
                            break
                        end
                    else
                        timer = Time.GetEpocheMs() + 10000
                    end
                end
            end
        end
        Script.Yield(100)
    end
    Script.Yield()
end)

