                if curVeh == cachedVehicle then
                    for i = 0, 49 do
                        local mod = VEHICLE.GET_VEHICLE_MOD(vehicle, i)
                        if cachedData.veh_mods[i] ~= mod and owner == driver and not isPlayerInInterior(playerID) and pos.z > 0.0 and PED.GET_VEHICLE_PED_IS_USING(ped) == vehicle and not moddedUpgradeDetected[playerID] then
                            if getEntityOwner(curVeh) ~= driver then
                                cachedModData[playerID].veh_mods[i] = nil
                                Script.Yield(5000)
                                return
                            end
                            
                            ModderDB.AddModderByPlayerId(playerID, "Modded Vehicle Upgrade")
                            moddedUpgradeDetected[playerID] = true
                            Script.Yield(500)
                            break
                        end
                        
                        cachedData.veh_mods[i] = mod
                    end
                end
                cachedModData[playerID] = cachedData
                cachedVehData[playerID] = curVeh
                ::continue::
            end
            Script.Yield(100)
        end
        Script.Yield()
    end)
end

