function getWeaponEventData(datBitBuffer, sender)
	local event = {}
	event.senderId = sender.PlayerId

	event.damageType = datBitBuffer:ReadUns(2)  -- "Damage Type"
	event.weaponHash = datBitBuffer:ReadUns(32) -- "Weapon Type"

	event.override = datBitBuffer:ReadBool()    --"Override Default Damage"
	-- write whether we really hit the weapon the entity (ped) is holding
	event.hitWeapon = datBitBuffer:ReadBool()   -- "Hit Entity Weapon"
	datBitBuffer:ReadBool()                     -- "Hit Weapon Ammo Attachment"
	datBitBuffer:ReadBool()                     -- "Silenced"

	event.flags = datBitBuffer:ReadUns(25)      -- "Damage flags"

	event.meleeDamage = datBitBuffer:ReadBool() -- "Has Melee Damage"
	if (event.meleeDamage) then
		datBitBuffer:ReadUns(32)                -- "Action Result ID"
		datBitBuffer:ReadUns(16)                -- "Melee ID"
		datBitBuffer:ReadUns(32)                -- "melee Reaction ID"
	end

	if (event.override) then
		event.damage = datBitBuffer:ReadUns(17) -- "Weapon Damage"
	end

	local bIsAggregated = datBitBuffer:ReadBool()
	if (bIsAggregated) then
		datBitBuffer:ReadUns(5) -- "Weapon Damage Aggregation Count"
	end

	event.victim = datBitBuffer:ReadBool()
	if (event.victim) then
		datBitBuffer:ReadUns(16) -- "Local Hit Position X" - packed float
		datBitBuffer:ReadUns(16) -- "Local Hit Position Y" - packed float
		datBitBuffer:ReadUns(16) -- "Local Hit Position Z" - packed float
	end

	if (eDamageType[event.damageType] == "DAMAGE_PLAYER") then
		datBitBuffer:ReadUns(32)             -- "Damage Time"
		event.shouldKill = datBitBuffer:ReadBool() -- "Will Kill"
		if (event.meleeDamage) then
			event.hitObjectID = datBitBuffer:ReadUns(13)
		end
	else
		event.parentID = datBitBuffer:ReadUns(13)
		event.hitObjectID = datBitBuffer:ReadUns(13)
	end

	if not event.damage then
		event.damage = WEAPON.GET_WEAPON_DAMAGE(WEAPON.GET_SELECTED_PED_WEAPON(GTA.PointerToHandle(Players.GetCPed(event
		.senderId))))                                                                                                           -- doesnt need a component :shrug
	end
	if event.weaponHash == 0 then
		event.weaponHash = WEAPON.GET_SELECTED_PED_WEAPON(GTA.PointerToHandle(Players.GetCPed(event.senderId)))
	end
	if event.damage == 0.0 then
		event.damage = WEAPON.GET_WEAPON_DAMAGE(event.weaponHash)
		if event.meleeDamage or event.damage == 0.0 then
			return false
		end
	end
	event.damage = event.damage * 1.0 -- convert to float
	return event
end

function isDamageAgainstMe(e)
	if e.victim and e.shouldKill ~= nil then -- in damage_player and damage_object possible
		return true, "Victim Flag set"
	end
	if eDamageType[e.damageType] == "DAMAGE_PLAYER" then
		if e.hitObjectID and e.hitObjectID == 0 and e.hitObjectID == getOwnObjectID() then
			return true, "hitObjectID is own ID"
		elseif e.hitObjectID and e.hitObjectID ~= 0 then
			return false      -- some melee damage shit cockstar does :shrug
		end
		if e.shouldKill ~= nil then -- VictimPlayer = static_cast<CPed*>(pHitEntity)->IsPlayer(); //
			return true, "shouldKill is set"
		end
	else
		if e.hitObjectID == getOwnObjectID() then
			return true, "hitObjectID is own ID"
		end
		if e.flags == 0 and e.victim or e.weaponHash == 0 then
			return true, "Damage Flags"
		end
	end
	return false
end

FeatureMgr.AddLoop(Utils.Joaat("RedirectWeaponDamage"), "Redirect Weapon Damage", eFeatureType.Toggle, "Description: Redirects weapon damage to the inflictor.", function()
    function redirectDamageToPlayer(event)
        if not FeatureMgr.GetFeatureIsEnabled(Utils.Joaat("RedirectWeaponDamage")) then 
			return 
		end

        if string.find(Players.GetTags(event.senderId), "[G]") then 
			return 
		end
        
        Script.QueueJob(function()
            if event.weaponHash == 0 then
                event.weaponHash = WEAPON.GET_SELECTED_PED_WEAPON(GTA.PointerToHandle(Players.GetCPed(event.senderId)))
            end
            
            if event.damage == 0.0 then
                event.damage = WEAPON.GET_WEAPON_DAMAGE(event.weaponHash)
                if event.meleeDamage or event.damage == 0.0 then return false end
            end
            
            if not event.senderId then
				return
			end
            
            local senderPedAddress = Players.GetCPed(event.senderId):GetAddress()
            local localPedAddress = Players.GetCPed(GTA.GetLocalPlayerId()):GetAddress()
            
            if not WEAPON.IS_WEAPON_VALID(event.weaponHash) then
            end
            
            local randomPed
            if PLAYER.IS_PLAYER_DEAD(GTA.GetLocalPlayerId()) or (event.shouldKill and WEAPON.GET_WEAPONTYPE_MODEL(event.weaponHash) ~= 0) then
                Memory.LuaCallCFunction(CWeaponDamageEvent__Trigger, localPedAddress, senderPedAddress, senderPedAddress + 0x90, 0, 1, event.weaponHash, event.damage, 0, 0, event.flags, 0, 0, 0, 0, 0, 0, 0, V3.New())
                
