function getFeatureType(ft)
	if ft == eFeatureType.InputInt or ft == eFeatureType.SliderInt or ft == eFeatureType.SliderIntToggle then
		return 'int'
	elseif ft == eFeatureType.InputFloat or ft == eFeatureType.SliderFloat or ft == eFeatureType.SliderFloatToggle then
		return 'float'
	else
		return 'other'
	end
end

function loadConfig()
	local configPath = FileMgr.GetMenuRootPath() .. "\\Lua\\JinxScript\\config.json"
    if not FileMgr.DoesFileExist(configPath) then
        return GUI.AddToast("JinxScript", "No config file found.", 5000)
    end
    local jsonData = JSON.decode(FileMgr.ReadFileContent(configPath))
    for stringHash, data in pairs(jsonData) do
        local hash = tonumber(stringHash)
        local feature = FeatureMgr.GetFeature(hash)
        if feature then				
            local featureType = getFeatureType(feature:GetType())  -- needed bc it fucks up with floats n ints
            if data["bool"] then
                feature:SetBoolValue(data["bool"]) -- for toggleInt, togglecombo etc
            end
            if feature:GetType() == eFeatureType.Combo or feature:GetType() == eFeatureType.ComboToggles then
                feature:SetListIndex(data["value"])
            else					
                if featureType == 'int' then
                    feature:SetIntValue(data["value"])
                elseif featureType == 'float' then
                    feature:SetFloatValue(data["value"])
                else
                    feature:SetValue(data["value"])
                end
            end
            feature:TriggerCallback()
        end
    end
    Logger.Log(eLogColor.CYAN, "JinxScript", "Successfully loaded JinxScript settings")
    GUI.AddToast("JinxScript", "Successfully loaded JinxScript settings", 5000)
end


function shouldAutoLoad()
	local configPath = FileMgr.GetMenuRootPath() .. "\\Lua\\JinxScript\\autoload"
	if not FileMgr.DoesFileExist(configPath) then
