    if not FileMgr.DoesFileExist(FileMgr.GetMenuRootPath() .. "\\Lua\\JinxScript") then
        FileMgr.CreateDir(FileMgr.GetMenuRootPath() .. "\\Lua\\JinxScript")
    end
    local data = {}
    for i = 1,#allFeaturesTable do
        local feature = allFeaturesTable[i]
        local featureType = feature:GetType()
        local hash = tostring(feature:GetHash())
        if featureType == eFeatureType.Button then		
        elseif featureType == eFeatureType.Toggle then
            data[hash] = { value = feature:GetBoolValue() }
        elseif featureType == eFeatureType.InputColor3 then
            data[hash] = { value = feature:GetColorU32() }
        elseif featureType == eFeatureType.InputColor4 then
            data[hash] = { value = feature:GetColorU32() }
        elseif featureType == eFeatureType.InputFloat then
            data[hash] = { value = feature:GetFloatValue() * 1.0 }
        elseif featureType == eFeatureType.SliderFloat then
            data[hash] = { value = feature:GetFloatValue() * 1.0 }
        elseif featureType == eFeatureType.InputInt then
            data[hash] = { value = feature:GetIntValue() }
        elseif featureType == eFeatureType.SliderInt then
            data[hash] = { value = feature:GetIntValue() }
        elseif featureType == eFeatureType.InputText then
            data[hash] = { value = feature:GetStringValue() }
        elseif featureType == eFeatureType.Combo then
            data[hash] = { value = feature:GetListIndex() }
        elseif featureType == eFeatureType.ComboToggles then
            data[hash] = {
                value = feature:GetListIndex(),
                bool = feature:GetBoolValue(),
            }
        elseif featureType == eFeatureType.SliderIntToggle then
            data[hash] = {
                value = feature:GetIntValue(),
                bool = feature:GetBoolValue(),
            }
        elseif featureType == eFeatureType.SliderFloatToggle then
            data[hash] = {
                value = feature:GetFloatValue() * 1.0,
                bool = feature:GetBoolValue(),
            }		
        elseif isDebugMode then
            Logger.LogError("Cant save data for " .. feature:GetName() .. " type: " .. featureType)
        end
    end

    local function encode_pretty(data, level)
        local function encode_value(value, level)
            local t = type(value)
            local s = ""
            if t == "table" then
                s = s .. "{\n"
                local sep = ""
                for k, v in pairs(value) do
                    s = s .. sep .. string.rep("    ", level) .. "\"" .. tostring(k) .. "\": " .. encode_value(v, level + 1)
                    sep = ",\n"
                end
                s = s .. "\n" .. string.rep("    ", level - 1) .. "}"
            elseif t == "string" then
                s = "\"" .. value .. "\""
            elseif t == "number" or t == "boolean" then
                s = tostring(value)
            else
                s = "null"
            end
            return s
        end
        return encode_value(data, level)
    end
    
    local jsonData = encode_pretty(data, 1)
    if FileMgr.WriteFileContent(configPath, tostring(jsonData)) then
        GUI.AddToast("JinxScript", "Successfully saved your JinxScript config", 5000)
        Logger.Log(eLogColor.CYAN, "JinxScript", "Successfully saved your JinxScript config")
    end
end, false)

