function findAndRemove(tbl, value)
    for i = #tbl, 1, -1 do
        if type(tbl[i]) == "string" and tbl[i]:find(value, 1, true) then
            table.remove(tbl, i)
            break
        end
    end
    return tbl
end
function addNamesTogether(str, name, isLastItem)
	name = tostring(name)
	if type(name) == "boolean" then return end
	if infoOverlay.indentWithDash then
		str = str .. "- " .. name
	else
		str = str .. name
	end
	if not isLastItem then
		str = str .. "\n"
	end
	return str
end

function updateInfoList()
	if infoOverlay.enabled then
		local hostPid = NETWORK.NETWORK_GET_HOST_PLAYER_INDEX()
		local nextHostPlayerID = Players.Get(ePlayerListSort.HOST_QUEUE)[2]

		local playerCount, modderCount, friendCount = 0, 0, 0

		for _, playerID in pairs(Players.Get()) do
			playerCount = playerCount + 1

			local playerTags = Players.GetTags(playerID)
			local playerName = Players.GetName(playerID)

			getNatType()

			if string.find(playerTags, "%[M%]") then
				modderCount = modderCount + 1
				if not infoLists.modderPids[playerID] and playerID ~= GTA.GetLocalPlayerId() then
					infoLists.modderPids[playerID] = true
					table.insert(infoLists.modderNames, playerName)
				end
			end

			if string.find(playerTags, "%[SH%]") then
				infoData.scripthost = (infoLists.modderPids[playerID] and playerID ~= GTA.GetLocalPlayerId()) and
				string.format("%s (Modding)", playerName) or playerName
			end

			if string.find(playerTags, "%[F%]") then
				friendCount = friendCount + 1
				if not infoLists.friendPids[playerID] then
					infoLists.friendPids[playerID] = true
					table.insert(infoLists.friendNames, playerName)
				end
			end

			if playerID ~= GTA.GetLocalPlayerId() then
				if isPlayerGodmode(playerID) then
					if not infoLists.godmodePids[playerID] and not infoLists.godmodeNames[playerName] then
						infoLists.godmodePids[playerID] = true
						table.insert(infoLists.godmodeNames, playerName)
					end
				else
					infoLists.godmodePids[playerID] = false
					infoLists.godmodeNames = findAndRemove(infoLists.godmodeNames, playerName)
				end
			end
		end

		infoData.host = (infoLists.modderPids[hostPid] and hostPid ~= GTA.GetLocalPlayerId()) and
		string.format("%s (Modding)", Players.GetName(hostPid)) or Players.GetName(hostPid)
		infoData.nextHost = nextHostPlayerID and
		string.format("%s%s", Players.GetName(nextHostPlayerID),
			(infoLists.modderPids[nextHostPlayerID] and nextHostPlayerID ~= GTA.GetLocalPlayerId()) and " (Modding)" or
			"") or "N/A"

		infoData.playerCount = playerCount
		infoData.modderCount = modderCount
		infoData.friendCount = friendCount

		local function formatNames(namesList)
			if #namesList >= 1 then
				local formattedNames = ""
				for i = 1, #namesList do
					formattedNames = addNamesTogether(formattedNames, namesList[i], i == #namesList)
				end
				return formattedNames
			else
				return "N/A"
			end
		end

		infoData.modderNames = formatNames(infoLists.modderNames)
		infoData.friendNames = formatNames(infoLists.friendNames)
		infoData.attackerNames = formatNames(infoLists.attackerNames)
		infoData.godmodeNames = formatNames(infoLists.godmodeNames)
	end
end

local sessionInfoFlags = ImGuiWindowFlags.AlwaysAutoResize | ImGuiWindowFlags.NoCollapse | ImGuiWindowFlags.NoFocusOnAppearing | ImGuiWindowFlags.NoDecoration
local styleType = 0
function drawInfoWindow()
	if not infoOverlay.enabled or not NETWORK.NETWORK_IS_SESSION_STARTED() then return end

	ImGui.PushStyleColor(2, infoOverlay.backgroundColor.R, infoOverlay.backgroundColor.G,
		infoOverlay.backgroundColor.B, infoOverlay.backgroundColor.A)

	if ImGui.Begin("Info Overlay", true, sessionInfoFlags) then
		if styleType == 0 then
			ImGui.Separator()
		elseif styleType == 1 then
			local numOfUnderscores = math.floor(ImGui.GetWindowWidth() / 5.23)
			local line = string.rep("_", numOfUnderscores)
			local cursorY = math.max(0, ImGui.GetCursorPosY() - 21)

			ImGui.SetCursorPosY(cursorY)
			ImGui.TextColored(infoOverlay.textColor.R, infoOverlay.textColor.G, infoOverlay.textColor.B, infoOverlay.textColor.A, line)
			ImGui.SetCursorPosY(cursorY - 1)
			ImGui.TextColored(infoOverlay.textColor.R, infoOverlay.textColor.G, infoOverlay.textColor.B, infoOverlay.textColor.A, line)
		end

		local function drawText(label, value, indent)
			if indent then ImGui.Indent() end
			ImGui.TextColored(infoOverlay.textColor.R, infoOverlay.textColor.G, infoOverlay.textColor.B, infoOverlay.textColor.A, label .. value)
			if indent then ImGui.Unindent() end
		end

		-- Session Info Items
		local infoItems = {
			{ infoOverlay.host, "Session Host: ", (infoData.host == "Invalid" or not infoData.host) and "N/A" or infoData.host },
			{ infoOverlay.nextHost, "Next Session Host: ", infoData.playerCount == 1 and "N/A" or (infoData.nextHost or "N/A") },
			{ infoOverlay.scripthost, "Script Host: ", infoData.scripthost or "N/A" },
			{ infoOverlay.playerCount, "Player Count: ", string.format("%d/32", infoData.playerCount or 1) },
			{ infoOverlay.natType, "NAT Type: ", infoData.natType or "Unknown" },
			{ infoOverlay.friendCount, "Friend Count: ", infoData.friendCount or 0 },
			{ infoOverlay.modderCount, "Modder Count: ", infoData.modderCount or 0 },
			{ infoOverlay.attackerCount, "Attack Count: ", infoData.attackerCount or 0 },
		}

		for _, item in ipairs(infoItems) do
			if item[1] then drawText(item[2], item[3]) end
		end

		local nameItems = {
			{ infoOverlay.friendNames, "Friends: ", infoData.friendNames or "N/A" },
			{ infoOverlay.modderNames, "Modders: ", infoData.modderNames or "N/A" },
			{ infoOverlay.attackerNames, "Attackers: ", infoData.attackerNames or "N/A" },
			{ infoOverlay.godmodeNames, "Godmode: ", infoData.godmodeNames or "N/A" },
		}

		for _, item in ipairs(nameItems) do
			if item[1] then
				if item[3] == "N/A" then
					drawText(item[2], item[3])
				else
					drawText(item[2], "", false)
					drawText("", item[3], true)
				end
			end
		end

		if styleType == 0 then ImGui.Separator() end
		ImGui.End()
	end

	ImGui.PopStyleColor()
end



