    local entitySpeed = (ENTITY.GET_ENTITY_SPEED(ped) * speedometer.unitSpeed)
    local vehMaxSpeed = ((VEHICLE.GET_VEHICLE_ESTIMATED_MAX_SPEED(vehicle) * speedometer.unitSpeed) + speedometer.leeway)
    local parachuteHandle = ENTITY.GET_ENTITY_ATTACHED_TO(ped)
    local parachuteSpeed = (isParachuting and ENTITY.GET_ENTITY_SPEED(parachuteHandle))
    local isFalling = PED.IS_PED_FALLING(ped) or TASK.GET_IS_TASK_ACTIVE(ped, 18) or TASK.GET_IS_TASK_ACTIVE(ped, 19)

    if not m_ScaleformHandle or not GRAPHICS.HAS_SCALEFORM_MOVIE_FILENAME_LOADED("DRAG_RACE") then
        m_ScaleformHandle = GRAPHICS.REQUEST_SCALEFORM_MOVIE("DRAG_RACE")
    end

    if STREAMING.IS_PLAYER_SWITCH_IN_PROGRESS() or isPlayerInCutscene(GTA.GetLocalPlayerId()) or isPlayerInInterior(GTA.GetLocalPlayerId()) or GTA.IsPhoneOpen() then
        return
    end

    if not isParachuting and not isInVehicle and not bRenderOnFoot then
        return
    end

    if isSpectating and not bRenderWhileSpectating then
        return
    end

    if isSpectating and bRenderWhileSpectating then
        if vehicle and vehicle ~= 0 then
            local pVehicle = GTA.HandleToPointer(vehicle):GetAddress()
            vehicleGear = Memory.ReadByte(pVehicle + 2178)
        end
    end

    if isInVehicle then
        meterValue = (ENTITY.GET_ENTITY_SPEED(vehicle) * speedometer.unitSpeed) / (vehMaxSpeed + speedometer.leeway)
        entitySpeed = (ENTITY.GET_ENTITY_SPEED(vehicle) * speedometer.unitSpeed)
    elseif PED.IS_PED_IN_PARACHUTE_FREE_FALL(ped) then
        meterValue = entitySpeed / (speedometer.speedUnit == "MPH" and 171 or 275)
    elseif parachuteHandle ~= 0 and isParachuting then
        entitySpeed = parachuteSpeed
        meterValue = fThrust > 1 and (parachuteSpeed / (40 * (fThrust / 1.47))) or (parachuteSpeed / 40) -- fix this to only apply such logic to our own ped
    elseif isFalling then
        meterValue = entitySpeed / (speedometer.speedUnit == "MPH" and 267 or 430)
    elseif not isParachuting and not isInVehicle then
        meterValue = entitySpeed / (speedometer.speedUnit == "MPH" and 21 or 34)
    end

    GRAPHICS.BEGIN_SCALEFORM_MOVIE_METHOD(m_ScaleformHandle, "SET_GEAR")
    GRAPHICS.SCALEFORM_MOVIE_METHOD_ADD_PARAM_INT(vehicleGear)
    GRAPHICS.END_SCALEFORM_MOVIE_METHOD()
    GRAPHICS.BEGIN_SCALEFORM_MOVIE_METHOD(m_ScaleformHandle, "SET_SPEED")
    GRAPHICS.SCALEFORM_MOVIE_METHOD_ADD_PARAM_FLOAT(entitySpeed)
    GRAPHICS.END_SCALEFORM_MOVIE_METHOD()
    GRAPHICS.BEGIN_SCALEFORM_MOVIE_METHOD(m_ScaleformHandle, "SET_SPEED_UNITS")
    GRAPHICS.BEGIN_TEXT_COMMAND_SCALEFORM_STRING("STRING")
    HUD.ADD_TEXT_COMPONENT_SUBSTRING_PLAYER_NAME(speedometer.speedUnit)
    GRAPHICS.END_TEXT_COMMAND_SCALEFORM_STRING()
    GRAPHICS.END_SCALEFORM_MOVIE_METHOD()
    GRAPHICS.BEGIN_SCALEFORM_MOVIE_METHOD(m_ScaleformHandle, "SET_METER_VALUE")
    GRAPHICS.SCALEFORM_MOVIE_METHOD_ADD_PARAM_FLOAT(meterValue)
    GRAPHICS.END_SCALEFORM_MOVIE_METHOD()
    GRAPHICS.BEGIN_SCALEFORM_MOVIE_METHOD(m_ScaleformHandle, "SET_OUTER_GOAL")
    GRAPHICS.SCALEFORM_MOVIE_METHOD_ADD_PARAM_FLOAT(-1.0)
    GRAPHICS.SCALEFORM_MOVIE_METHOD_ADD_PARAM_FLOAT(-1.0)
    GRAPHICS.END_SCALEFORM_MOVIE_METHOD()
    GRAPHICS.BEGIN_SCALEFORM_MOVIE_METHOD(m_ScaleformHandle, "SET_INNER_GOAL")
    GRAPHICS.SCALEFORM_MOVIE_METHOD_ADD_PARAM_FLOAT(-1.0)
    GRAPHICS.SCALEFORM_MOVIE_METHOD_ADD_PARAM_FLOAT(-1.0)
    GRAPHICS.END_SCALEFORM_MOVIE_METHOD()
    GRAPHICS.BEGIN_SCALEFORM_MOVIE_METHOD(m_ScaleformHandle, "SET_SCREEN_POSITION")
    GRAPHICS.SCALEFORM_MOVIE_METHOD_ADD_PARAM_FLOAT(1.0)
    GRAPHICS.SCALEFORM_MOVIE_METHOD_ADD_PARAM_FLOAT(0.85)
    GRAPHICS.END_SCALEFORM_MOVIE_METHOD()
    GRAPHICS.BEGIN_SCALEFORM_MOVIE_METHOD(m_ScaleformHandle, "SET_IS_DRIFT_RACE")
    GRAPHICS.SCALEFORM_MOVIE_METHOD_ADD_PARAM_BOOL(false)
    GRAPHICS.END_SCALEFORM_MOVIE_METHOD()
    GRAPHICS.DRAW_SCALEFORM_MOVIE_FULLSCREEN(m_ScaleformHandle, 255, 255, 255, 255, 0)
end)

