FeatureMgr.AddLoop(Utils.Joaat("Ragebot"), "Ragebot", eFeatureType.Toggle, "Description: Automatically shoots at players within the selected distance.", function()
	for _, playerID in pairs(Players.Get()) do
		if playerID ~= GTA.GetLocalPlayerId() then
			local myPos = getPlayerPosition(GTA.GetLocalPlayerId())
			local pos = getPlayerPosition(playerID)
			local wpn = WEAPON.GET_SELECTED_PED_WEAPON(getPlayerPed())
			local dmg = WEAPON.GET_WEAPON_DAMAGE(wpn, 0)
			local shotDelay = math.floor(WEAPON.GET_WEAPON_TIME_BETWEEN_SHOTS(wpn) * 500)
