				while PAD.IS_CONTROL_PRESSED(0, 92) and not ShouldUnload() do
					ragebotEnabled = true
					myPos = getPlayerPosition(GTA.GetLocalPlayerId())
					pos = getPlayerPosition(playerID)
					PED.SET_PED_SHOOTS_AT_COORD(getPlayerPed(), pos.x, pos.y, pos.z + 0.4, true)
					WEAPON.REFILL_AMMO_INSTANTLY(getPlayerPed())
					Script.Yield(shotDelay)
				end
				ragebotEnabled = false
				Natives.InvokeVoid(0x5E6CC07646BBEAB8, GTA.GetLocalPlayerId(), false)
			end
		end
	end
end)

