allWeapons = {}
myWeapons = {}
function GetWeaponStuff()
    local playerPed = getPlayerPed()

    if playerPed and playerPed ~= 0 and not ShouldUnload() then
        local temp = {}

        for category, weapons in pairs(allweaponCategoriesAndNames) do
            for _, weaponInfo in ipairs(weapons) do
                local weaponName = weaponInfo[1]
                local hasWeapon = WEAPON.HAS_PED_GOT_WEAPON(playerPed, Utils.Joaat(weaponName), false)

                myWeapons[weaponName] = hasWeapon and weaponName or nil

                FeatureMgr.GetFeature(Utils.Joaat("give" .. weaponName)):SetVisible(not hasWeapon)
                FeatureMgr.GetFeature(Utils.Joaat("remove" .. weaponName)):SetVisible(hasWeapon)

                local weaponData = allWeapons[weaponName]
                if hasWeapon then
                    if weaponData.possibleAttachments then
                        FeatureMgr.GetFeature(Utils.Joaat("attachmentsCombo" .. weaponName)):SetVisible(true)
                    end
                    if weaponData.possibleCamos then
                        FeatureMgr.GetFeature(Utils.Joaat("camoCombo" .. weaponName)):SetVisible(true)
                    end
                    if weaponData.tintType ~= "none" then
                        FeatureMgr.GetFeature(Utils.Joaat("tintCombo" .. weaponName)):SetVisible(true)
                        FeatureMgr.GetFeature(Utils.Joaat("setTint" .. weaponName)):SetVisible(true)
                    end
                    if weaponData.tintType == "tintsMK2s" then
                        FeatureMgr.GetFeature(Utils.Joaat("mk2TintsCombo" .. weaponName)):SetVisible(true)
                        FeatureMgr.GetFeature(Utils.Joaat("mk2TintsSet" .. weaponName)):SetVisible(true)
                    end
                else
                    if weaponData.possibleAttachments then
                        FeatureMgr.GetFeature(Utils.Joaat("attachmentsCombo" .. weaponName)):SetVisible(false)
                        FeatureMgr.GetFeature(Utils.Joaat("addAttachment" .. weaponName)):SetVisible(false)
                        FeatureMgr.GetFeature(Utils.Joaat("removeAttachment" .. weaponName)):SetVisible(false)
                    end
                    if weaponData.possibleCamos then
                        FeatureMgr.GetFeature(Utils.Joaat("addCamo" .. weaponName)):SetVisible(false)
                        FeatureMgr.GetFeature(Utils.Joaat("removeCamo" .. weaponName)):SetVisible(false)
                        if #weaponData.possibleCamos > 1 then
                            FeatureMgr.GetFeature(Utils.Joaat("camoCombo" .. weaponName)):SetVisible(false)
                        end
                    end
                    if weaponData.tintType ~= "none" then
                        FeatureMgr.GetFeature(Utils.Joaat("tintCombo" .. weaponName)):SetVisible(false)
                        FeatureMgr.GetFeature(Utils.Joaat("setTint" .. weaponName)):SetVisible(false)
                    end
                    if weaponData.tintType == "tintsMK2s" then
                        FeatureMgr.GetFeature(Utils.Joaat("mk2TintsCombo" .. weaponName)):SetVisible(false)
                        FeatureMgr.GetFeature(Utils.Joaat("mk2TintsSet" .. weaponName)):SetVisible(false)
                    end
                end
            end
        end

        Script.Yield()

        for weaponName, _ in pairs(myWeapons) do
            local weaponData = allWeapons[weaponName]
            
            if weaponData.possibleAttachments then
                local currentComboIndex = FeatureMgr.GetFeature(Utils.Joaat("attachmentsCombo" .. weaponName)):GetListIndex() + 1
                for _, attachment in ipairs(weaponData.possibleAttachments) do
                    local attachmentName = attachment[1]
                    local hasAttachment = WEAPON.HAS_PED_GOT_WEAPON_COMPONENT(playerPed, Utils.Joaat(weaponName), Utils.Joaat(attachmentName))

                    if hasAttachment then
                        table.insert(temp, attachment)
                        if attachment == weaponData.possibleAttachments[currentComboIndex] then
                            FeatureMgr.GetFeature(Utils.Joaat("addAttachment" .. weaponName)):SetVisible(false)
                            FeatureMgr.GetFeature(Utils.Joaat("removeAttachment" .. weaponName)):SetVisible(true)
                        end
                    else
                        if attachment == weaponData.possibleAttachments[currentComboIndex] then
                            FeatureMgr.GetFeature(Utils.Joaat("addAttachment" .. weaponName)):SetVisible(true)
                            FeatureMgr.GetFeature(Utils.Joaat("removeAttachment" .. weaponName)):SetVisible(false)
                        end
                    end
                end
                weaponData.currentAttachments, temp = temp, {}
            end

            if weaponData.possibleCamos then
                local currentComboIndex = FeatureMgr.GetFeature(Utils.Joaat("camoCombo" .. weaponName)):GetListIndex() + 1
                for _, camo in ipairs(weaponData.possibleCamos) do
                    local camoName = camo[1]
                    local hasCamo = WEAPON.HAS_PED_GOT_WEAPON_COMPONENT(playerPed, Utils.Joaat(weaponName), Utils.Joaat(camoName))

                    if hasCamo then
                        if camo == weaponData.possibleCamos[currentComboIndex] then
                            FeatureMgr.GetFeature(Utils.Joaat("addCamo" .. weaponName)):SetVisible(false)
                            FeatureMgr.GetFeature(Utils.Joaat("removeCamo" .. weaponName)):SetVisible(true)
                        end
                        weaponData.camoEquipped = camo
                        weaponData.camoTintEquipped = WEAPON.GET_PED_WEAPON_COMPONENT_TINT_INDEX(playerPed, Utils.Joaat(weaponName), Utils.Joaat(camoName))
                    else
                        if camo == weaponData.possibleCamos[currentComboIndex] then
                            FeatureMgr.GetFeature(Utils.Joaat("addCamo" .. weaponName)):SetVisible(true)
                            FeatureMgr.GetFeature(Utils.Joaat("removeCamo" .. weaponName)):SetVisible(false)
                        end
                        weaponData.camoEquipped = -1
                    end
                end
            end
            weaponData.tintEquipped = WEAPON.GET_PED_WEAPON_TINT_INDEX(playerPed, Utils.Joaat(weaponName))
        end
    end
    Script.Yield(500)
end


function giveLoadOut()
    local LoadoutPath = FileMgr.GetMenuRootPath() .. "\\Lua\\JinxScript\\loadout.txt"

    if not FileMgr.DoesFileExist(LoadoutPath) then
        Logger.LogInfo("[JinxScript] No Weapon Loadout Detected")
        GUI.AddToast("JinxScript", "No Weapon Loadout Detected :/", 5000)
        return
    end

    local loadoutContent = FileMgr.ReadFileContent(LoadoutPath)
    if not loadoutContent or loadoutContent == "" or string.match(loadoutContent, "[\\%(%).]") then
        if isDebugMode then
            Logger.LogError("Broken loadout file detected!")
        end
        return
    end

    local loadout = load(loadoutContent)()
    Script.Yield(250)

    local ped = getPlayerPed()
    WEAPON.REMOVE_ALL_PED_WEAPONS(ped)

    for weaponName, weaponData in pairs(loadout) do
        local weaponHash = Utils.Joaat(weaponName)

        if not WEAPON.IS_WEAPON_VALID(weaponHash) then
            if isDebugMode then
                Logger.LogError("Invalid weapon hash '" .. weaponHash .. "' in loadout")
            end
            return
        end

        WEAPON.GIVE_WEAPON_TO_PED(ped, weaponHash, 69, true, false)
        Script.Yield()

        WEAPON.SET_PED_WEAPON_TINT_INDEX(ped, weaponHash, weaponData.TintIndex)

        if weaponData.Camo and weaponData.Camo ~= "" and weaponData.Camo ~= "-1" then
            WEAPON.GIVE_WEAPON_COMPONENT_TO_PED(ped, weaponHash, Utils.Joaat(weaponData.Camo))
            
            if weaponData.CamoTint and weaponData.CamoTint ~= "" and weaponData.CamoTint ~= "-1" then
                WEAPON.SET_PED_WEAPON_COMPONENT_TINT_INDEX(ped, weaponHash, Utils.Joaat(weaponData.Camo), tonumber(weaponData.CamoTint))
            end
        end

        if weaponData.Components and type(weaponData.Components) == "table" then
            for _, component in ipairs(weaponData.Components) do
                WEAPON.GIVE_WEAPON_COMPONENT_TO_PED(ped, weaponHash, Utils.Joaat(component))
            end
        end
    end

    Logger.LogInfo("[JinxScript] Loaded Weapon Loadout")
    GUI.AddToast("JinxScript", "Loaded Weapon Loadout", 5000)
end


function saveLoadOut()
    local LoadoutPath = FileMgr.GetMenuRootPath() .. "\\Lua\\JinxScript\\loadout.txt"

    if not FileMgr.DoesFileExist(LoadoutPath) then
        FileMgr.CreateDir(FileMgr.GetMenuRootPath() .. "\\Lua\\JinxScript")
    end
    FileMgr.WriteFileContent(LoadoutPath, "return {\n")

    for weaponName in pairs(myWeapons) do
        local weapon = allWeapons[weaponName]
        FileMgr.WriteFileContent(LoadoutPath, "\t[\"" .. weaponName .. "\"] = {\n", true)
        FileMgr.WriteFileContent(LoadoutPath, "\t\tTintIndex = " .. (weapon.tintEquipped or "0") .. ",\n", true)
        FileMgr.WriteFileContent(LoadoutPath, "\t\tCamo = \"", true)
        if weapon.camoEquipped and weapon.camoEquipped ~= -1 and weapon.camoEquipped[1] then
            FileMgr.WriteFileContent(LoadoutPath, weapon.camoEquipped[1] .. "\",\n", true)
            FileMgr.WriteFileContent(LoadoutPath, "\t\tCamoTint = " .. (weapon.camoTintEquipped or "-1") .. ",\n", true)
        else
            FileMgr.WriteFileContent(LoadoutPath, "-1\",\n", true)
            FileMgr.WriteFileContent(LoadoutPath, "\t\tCamoTint = -1,\n", true)
        end

        FileMgr.WriteFileContent(LoadoutPath, "\t\tComponents = {\n", true)
        if weapon.currentAttachments then
            if #weapon.currentAttachments == 0 and isDebugMode then
                Logger.LogError("No attachments for " .. weaponName)
            else
                for _, attachment in ipairs(weapon.currentAttachments) do
                    FileMgr.WriteFileContent(LoadoutPath, "\t\t\t\"" .. attachment[1] .. "\",\n", true)
                end
            end
        end
        FileMgr.WriteFileContent(LoadoutPath, "\t\t},\n", true)
        FileMgr.WriteFileContent(LoadoutPath, "\t},\n", true)
    end

    FileMgr.WriteFileContent(LoadoutPath, "}", true)
    Logger.LogInfo("[JinxScript] Saved Weapon Loadout")
    GUI.AddToast("JinxScript", "Saved Weapon Loadout", 5000)
end

function renderAttachmentOptions(category)
    local weapons = allweaponCategoriesAndNames[category]
    
    for i = 1, #weapons do
        local weaponData = weapons[i]
        local weaponHash = weaponData[1]
        local weaponName = tostring(weaponData[2])

        if ImGui.TreeNode(weaponName) then
            if not myWeapons[weaponHash] then
                ClickGUI.RenderFeature(Utils.Joaat("give" .. weaponHash))
            else
                ClickGUI.RenderFeature(Utils.Joaat("GiveAmmo" .. weaponHash))
                ClickGUI.RenderFeature(Utils.Joaat("remove" .. weaponHash))

                renderAttachments(weaponHash)
                renderTints(weaponHash)
                renderCamos(weaponHash)
            end

            ImGui.Separator()
            ImGui.TreePop()
        end
    end
end

