    local weapon = allWeapons[weaponHash]
    
    if weapon.possibleAttachments and type(weapon.possibleAttachments) == "table" then
        if #weapon.possibleAttachments == 1 then
            ClickGUI.RenderFeature(Utils.Joaat("addAttachment" .. weaponHash))
        elseif #weapon.possibleAttachments >= 2 then
            ImGui.Separator()
            ClickGUI.RenderFeature(Utils.Joaat("attachmentsCombo" .. weaponHash))
            ClickGUI.RenderFeature(Utils.Joaat("removeAttachment" .. weaponHash))
            ClickGUI.RenderFeature(Utils.Joaat("addAttachment" .. weaponHash))
            ImGui.Separator()
        end
    end
end

