function weaponManagerInit()
    local startTime = Time.GetEpocheMs()
    local weaponCount = 0
    
    for category, weapons in pairs(allweaponCategoriesAndNames) do
        for _, weapon in ipairs(weapons) do
            weaponCount = weaponCount + 1
            
            local weaponHashName = weapon[1]
            local weaponHash = Utils.Joaat(weaponHashName)
            
            allWeapons[weaponHashName] = {
                tintType = getWeaponTintType(weaponHash),
                tintSelected = 1,
                tintEquipped = 1,
                camoSelected = 1,
                attachmentSelected = 1
            }
            
            if allWeapons[weaponHashName].tintType == "tintsMK2s" then
                allWeapons[weaponHashName].camoTintSelected = 1
                allWeapons[weaponHashName].camoTintEquipped = -1
            end
            
            assignWeaponComponents(weaponHash, weaponHashName)
        end
    end
    
    Script.RegisterLooped(GetWeaponStuff)
end

function getWeaponTintType(weaponHash)
    local tintCount = WEAPON.GET_WEAPON_TINT_COUNT(weaponHash)
    
    local tintTypes = {
        [1] = "none",
        [7] = "tintsRays",
        [8] = "tintsNormal",
        [33] = "tintsMK2s"
    }
    
    return tintTypes[tintCount] or logUnknownTint(weaponHash, tintCount)
end

function logUnknownTint(weaponHash, tintCount)
    if isDebugMode then
        Logger.LogError("[IWT] Unknown Weapon Tints " .. weaponHash .. " | " .. tintCount)
    end
    return "unk"
end

function assignWeaponComponents(weaponHash, weaponHashName)
    for _, component in ipairs(allWeaponComponents) do
        if WEAPON.DOES_WEAPON_TAKE_WEAPON_COMPONENT(weaponHash, Utils.Joaat(component[1])) then
            if component[1]:match("CAMO") or component[1]:match("VARMOD") then
                addCamoComponent(weaponHashName, component)
            else
                addAttachmentComponent(weaponHashName, component)
            end
        end
    end
end

function addCamoComponent(weaponHashName, component)
    allWeapons[weaponHashName].possibleCamos = allWeapons[weaponHashName].possibleCamos or {}
    allWeapons[weaponHashName].possibleCamoNames = allWeapons[weaponHashName].possibleCamoNames or {}
