FeatureMgr.AddFeature(Utils.Joaat("TeleportToMyWaypoint"), "Teleport To Waypoint", eFeatureType.Button, "Description: Teleports you to your waypoint with the selected teleport type.", function(feature)
    if not HUD.IS_WAYPOINT_ACTIVE() then
        GUI.AddToast("JinxScript", "No waypoint was found. :/", 5000)
        return
    end

    local vehicle = PED.GET_VEHICLE_PED_IS_USING(getPlayerPed())
    local waypoint = HUD.GET_BLIP_INFO_ID_COORD(HUD.GET_FIRST_BLIP_INFO_ID(HUD.GET_WAYPOINT_BLIP_ENUM_ID()))
    local ground = false
    repeat
        ground, groundZ = GTA.GetGroundZ(waypoint.x, waypoint.y)
        Script.Yield()
    until ShouldUnload() or ground
    waypoint.z = groundZ

    if teleportType == 0 then
        NETWORK.NETWORK_FADE_OUT_ENTITY(getPlayerPed(), true, false)
        if vehicle ~= 0 then
            NETWORK.NETWORK_FADE_OUT_ENTITY(vehicle, true, false)
        end

        STREAMING.SWITCH_TO_MULTI_FIRSTPART(getPlayerPed(), 8, 1)
        HUD.BEGIN_TEXT_COMMAND_BUSYSPINNER_ON("PM_WAIT")
        HUD.END_TEXT_COMMAND_BUSYSPINNER_ON(4)

        repeat
            Script.Yield()
        until ShouldUnload() or STREAMING.IS_SWITCH_TO_MULTI_FIRSTPART_FINISHED()

        if vehicle == 0 then
            ENTITY.SET_ENTITY_COORDS_NO_OFFSET(getPlayerPed(), waypoint.x, waypoint.y, waypoint.z, false, false, false)
        else
            ENTITY.SET_ENTITY_VISIBLE(vehicle, false)
            ENTITY.SET_ENTITY_COORDS_NO_OFFSET(vehicle, waypoint.x, waypoint.y, waypoint.z, false, false, false)
        end

        local zPos = 0
        STREAMING.SWITCH_TO_MULTI_SECONDPART(getPlayerPed())
        STREAMING.ALLOW_PLAYER_SWITCH_OUTRO() 

        repeat
            ENTITY.SET_ENTITY_VISIBLE(getPlayerPed(), false)
            Script.Yield()
        until ShouldUnload() or not STREAMING.IS_PLAYER_SWITCH_IN_PROGRESS()

        if vehicle == 0 then
            NETWORK.NETWORK_FADE_IN_ENTITY(getPlayerPed(), true, true)
        else
            NETWORK.NETWORK_FADE_IN_ENTITY(vehicle, true, 1)
            NETWORK.NETWORK_FADE_IN_ENTITY(getPlayerPed(), true, true)
            ENTITY.SET_ENTITY_VISIBLE(vehicle, true)
        end

        HUD.BUSYSPINNER_OFF()
    elseif teleportType == 1 then
        NETWORK.NETWORK_FADE_OUT_ENTITY(getPlayerPed(), true, false)
        if vehicle ~= 0 then
            NETWORK.NETWORK_FADE_OUT_ENTITY(vehicle, true, false)
        end
    
        CAM.DO_SCREEN_FADE_OUT(fadeOut)
        Script.Yield(fadeOut)
        if vehicle == 0 then
            ENTITY.SET_ENTITY_COORDS_NO_OFFSET(getPlayerPed(), waypoint.x, waypoint.y, waypoint.z, false, false, false)
        else
            ENTITY.SET_ENTITY_VISIBLE(vehicle, false)
            ENTITY.SET_ENTITY_COORDS_NO_OFFSET(vehicle, waypoint.x, waypoint.y, waypoint.z, false, false, false)
        end

        Script.Yield(fadeIn)
        CAM.DO_SCREEN_FADE_IN(fadeIn)

        if vehicle == 0 then
            NETWORK.NETWORK_FADE_IN_ENTITY(getPlayerPed(), true, true)
        else
            NETWORK.NETWORK_FADE_IN_ENTITY(vehicle, true, 1)
            NETWORK.NETWORK_FADE_IN_ENTITY(getPlayerPed(), true, true)
            ENTITY.SET_ENTITY_VISIBLE(vehicle, true)
        end
    end
end)

