local pedTable = {}
FeatureMgr.AddPlayerFeature(Utils.Joaat("HijackVehicle"), "Hijack Vehicle", eFeatureType.Button, "Description: Spawns a ped to hijack their vehicle and drive away.", function(feature)
	local timer = Time.GetEpocheMs() + 2500
	local playerID = feature:GetPlayerIndex()
	local ped = PLAYER.GET_PLAYER_PED_SCRIPT_INDEX(playerID)
	local pos = getPlayerPosition(playerID)
	local vehicle = PED.GET_VEHICLE_PED_IS_USING(ped)
	local drivingStyle = eDrivingMode.DF_SwerveAroundAllCars | eDrivingMode.DF_AvoidRestrictedAreas | eDrivingMode.DF_GoOffRoadWhenAvoiding | eDrivingMode.DF_SteerAroundObjects | eDrivingMode.DF_UseShortCutLinks | eDrivingMode.DF_ChangeLanesAroundObstructions
	local ping = math.floor(NETWORK.NETWORK_GET_AVERAGE_PING(playerID))
	
	if PLAYER.IS_REMOTE_PLAYER_IN_NON_CLONED_VEHICLE(playerID) then
		GUI.AddToast("JinxScript", Players.GetName(playerID) .. "'s vehicle has not been cloned yet. :/", 5000)
		return
	end

	if not PED.IS_PED_IN_ANY_VEHICLE(ped) then
		GUI.AddToast("JinxScript", Players.GetName(playerID) .. " is not in any vehicle. :/", 5000)
		return
	end

	pos.z = pos.z - 30
	if not PED.IS_PED_A_PLAYER(VEHICLE.GET_PED_IN_VEHICLE_SEAT(vehicle, -1, false)) then
		GUI.AddToast("JinxScript", Players.GetName(playerID) .. "'s vehicle has already been hijacked. :D", 5000)
		return 
	end
	
	pedTable[playerID] = GTA.CreateRandomPed(pos.x, pos.y, pos.z)
	ENTITY.SET_ENTITY_INVINCIBLE(pedTable[playerID], true)
	PED.SET_BLOCKING_OF_NON_TEMPORARY_EVENTS(pedTable[playerID], true)
	setEntityCanMigrate(pedTable[playerID], false)
	TASK.TASK_ENTER_VEHICLE(pedTable[playerID], vehicle, 1000, -1, 1.0, eEnterExitVehicleFlags.ECF_WARP_ENTRY_POINT | eEnterExitVehicleFlags.ECF_DONT_WAIT_FOR_VEHICLE_TO_STOP | eEnterExitVehicleFlags.ECF_JACK_ANYONE | eEnterExitVehicleFlags.ECF_WARP_PED | eEnterExitVehicleFlags.ECF_WARP_IF_DOOR_IS_BLOCKED, 0, false)
	
	while not TASK.GET_IS_TASK_ACTIVE(pedTable[playerID], 160) and not ShouldUnload() do
		if Time.GetEpocheMs() > timer then
			if isDebugMode then
				GUI.AddToast("JinxScript", "Failed to assign CTaskEnterVehicle to ped. :/", 5000)
			else
				GUI.AddToast("JinxScript", "Failed to hijack their vehicle. :/", 5000)
			end
			deleteEntity(pedTable[playerID])
			pedTable[playerID] = nil
			return
		end
		Script.Yield()
	end

	repeat
		if TASK.GET_IS_TASK_ACTIVE(ped, 2) then
			timer = Time.GetEpocheMs() + 4000
		end
		if Time.GetEpocheMs() > timer and PED.IS_PED_IN_ANY_VEHICLE(ped) then
			if ping > 80 then -- this is high enough to interfere with the hijack process
				GUI.AddToast("JinxScript", "Failed to hijack their vehicle due to high ping (" .. ping .. "ms). :/", 5000)
			else
				GUI.AddToast("JinxScript", "Failed to hijack their vehicle. :/", 5000)
			end
			deleteEntity(pedTable[playerID])
			pedTable[playerID] = nil
			timer = Time.GetEpocheMs() + 4000
			return 
		end
		Script.Yield()
	until ShouldUnload() or not PED.IS_PED_IN_ANY_VEHICLE(ped)

	if getSeatPedIsIn(pedTable[playerID]) == -1 then
		requestControl(vehicle, 2500)
		TASK.TASK_VEHICLE_DRIVE_WANDER(pedTable[playerID], vehicle, 9999.0, drivingStyle) 
		GUI.AddToast("JinxScript", "Bippity boppity their car is now your property :D", 5000)
		if not VEHICLE.GET_VEHICLE_DOORS_LOCKED_FOR_PLAYER(vehicle, playerID) then
			VEHICLE.SET_VEHICLE_DOORS_LOCKED_FOR_PLAYER(vehicle, playerID, true)
		end
	end

	if not TASK.GET_IS_TASK_ACTIVE(pedTable[playerID], 151) then
		if not PED.IS_PED_IN_ANY_VEHICLE(pedTable[playerID]) then
			repeat
				if Time.GetEpocheMs() > timer then
					if isDebugMode then
						GUI.AddToast("JinxScript", "Failed to assign CTaskEnterVehicle to ped. :/", 5000)
					else
						GUI.AddToast("JinxScript", "Failed to drive their vehicle away. :/", 5000)
					end
					deleteEntity(pedTable[playerID])
					pedTable[playerID] = nil
					return
				end
				PED.SET_PED_INTO_VEHICLE(pedTable[playerID], vehicle, -1)
				Script.Yield()
			until ShouldUnload() or PED.GET_VEHICLE_PED_IS_USING(pedTable[playerID]) == vehicle
			requestControl(vehicle, 2500)
			TASK.TASK_VEHICLE_DRIVE_WANDER(pedTable[playerID], vehicle, 9999.0, drivingStyle)
		end
	end
	Script.Timeout(5000)
	if pedTable[playerID] ~= nil and not PED.IS_PED_IN_ANY_VEHICLE(pedTable[playerID], false) then -- 2nd check cus sometimes doesnt delete the first time
		deleteEntity(pedTable[playerID])
		pedTable[playerID] = nil
	end
end)

local tp = false
FeatureMgr.AddPlayerFeature(Utils.Joaat("TakeoverVehicle"), "Takeover Vehicle", eFeatureType.Button, "Description: Spawns a ped to drag them out of their vehicle and place you in it.", function(feature)
