	if getSeatPedIsIn(randomPed) ~= -1 then
		repeat
			camPos = Players.GetCam(playerID)
			PED.SET_PED_INTO_VEHICLE(randomPed, vehicle, -1)
			if tp then
				ENTITY.SET_ENTITY_COLLISION(getPlayerPed(), false, false)
				invisibility:SetValue(true):TriggerCallback()
				ENTITY.SET_ENTITY_COORDS_NO_OFFSET(getPlayerPed(), camPos.x, camPos.y, camPos.z, false, false, false)
			end
			if TASK.GET_IS_TASK_ACTIVE(ped, 2) then
				timer = Time.GetEpocheMs() + 5000
			end
			if Time.GetEpocheMs() > timer then
				if tp then
					ENTITY.FREEZE_ENTITY_POSITION(getPlayerPed(), false)
					ENTITY.SET_ENTITY_COLLISION(getPlayerPed(), true, true)
					invisibility:SetValue(false):TriggerCallback()
					ENTITY.SET_ENTITY_COORDS_NO_OFFSET(getPlayerPed(), localPos.x, localPos.y, localPos.z, false, false, false)
					tp = false
				end
				if Time.GetEpocheMs() > timer then
					if ping > 80 then
						GUI.AddToast("JinxScript", "Failed to hijack their vehicle due to high ping (" .. ping .. "ms). :/", 5000)
						invisibility:SetValue(false):TriggerCallback()
					else
						GUI.AddToast("JinxScript", "Failed to takeover their vehicle. :/", 5000)
						invisibility:SetValue(false):TriggerCallback()
					end
				end
				deleteEntity(randomPed)
				timer = Time.GetEpocheMs() + 2500
				return
			end
			Script.Yield()
		until ShouldUnload() or getSeatPedIsIn(ped) ~= -1
		deleteEntity(randomPed)
	end
	timer = Time.GetEpocheMs() + 2500
	PED.SET_PED_INTO_VEHICLE(getPlayerPed(), vehicle, -1)
	repeat
		if not PED.IS_PED_IN_ANY_VEHICLE(ped) and getSeatPedIsIn(ped) ~= -1 then
			if tp then
				invisibility:SetValue(true):TriggerCallback()
			end
			if Time.GetEpocheMs() > timer then
				if DECORATOR.DECOR_GET_INT(vehicle, "Player_Vehicle") ~= 0 then
					GUI.AddToast("JinxScript", "Failed to warp into vehicle due to personal vehicle permissions. :/", 5000)
				else 
					GUI.AddToast("JinxScript", "Failed to warp into player vehicle. :/", 5000)
				end
				if tp then
					ENTITY.SET_ENTITY_COLLISION(getPlayerPed(), true, true)
					ENTITY.FREEZE_ENTITY_POSITION(getPlayerPed(), false)
					ENTITY.SET_ENTITY_COORDS_NO_OFFSET(getPlayerPed(), localPos.x, localPos.y, localPos.z, false, false, false)
					invisibility:SetValue(false):TriggerCallback()
					tp = false
				end
				deleteEntity(randomPed)
				timer = Time.GetEpocheMs() + 2500
				return
			end
			PED.SET_PED_INTO_VEHICLE(getPlayerPed(), vehicle, -1)
		end
		Script.Yield()
	until ShouldUnload() or getUserVehicle() == vehicle
	GUI.AddToast("JinxScript", "Bippity boppity their car is now your property :D", 5000)
	ENTITY.SET_ENTITY_COLLISION(getPlayerPed(), true, true)
	ENTITY.FREEZE_ENTITY_POSITION(getPlayerPed(), false)
	invisibility:SetValue(false):TriggerCallback()
