	local ped = PLAYER.GET_PLAYER_PED_SCRIPT_INDEX(playerID)
	local vehicle = PED.GET_VEHICLE_PED_IS_USING(ped)

	if PLAYER.IS_REMOTE_PLAYER_IN_NON_CLONED_VEHICLE(playerID) then
		GUI.AddToast("JinxScript", Players.GetName(playerID) .. "'s vehicle has not been cloned yet. :/", 5000)
