FeatureMgr.AddPlayerFeature(Utils.Joaat("HospitalizePlayer"), "Hospitalize", eFeatureType.Button, "Description: Replicates the orbital cannon without the explosion.", function(feature)
    local playerID = feature:GetPlayerIndex()
