    clearBit(GlobalplayerBD[GTA.GetLocalPlayerId()].iOrbitalCannonBS, 0)
end)


FeatureMgr.AddPlayerFeature(Utils.Joaat("KillPassive"), "Passive Kill", eFeatureType.Button, "Description: Kills the player through passive mode.", function(feature)
    local playerID = feature:GetPlayerIndex()

    if PLAYER.IS_PLAYER_DEAD(playerID) then 
        return 
    end

    if not isPlayerInPassiveMode(playerID) then
        GUI.AddToast("JinxScript", Players.GetName(playerID) .. " is not in passive mode. :/", 5000)
        return
    end

    local CPed = Players.GetCPed(playerID):GetAddress()
    local pos = getPlayerPosition(playerID)

    local randomPed = GTA.CreateRandomPed(pos.x, pos.y, pos.z - 50)
    local randomPedPtr = GTA.HandleToPointer(randomPed):GetAddress()

    setEntityCanMigrate(randomPed, false)
    ENTITY.FREEZE_ENTITY_POSITION(randomPed, true)
    
    Script.Yield(500)

    if CPed and CPed ~= 0 then
        Memory.LuaCallCFunction(CWeaponDamageEvent__Trigger, randomPedPtr, CPed, CPed + 0x90, 0, 1, Utils.sJoaat("weapon_heavysniper_mk2"), 2500.0, 0, 0, eDamageFlags.DF_IgnorePedFlags | eDamageFlags.DF_SuppressImpactAudio | eDamageFlags.DF_IgnoreRemoteDistCheck, 0, 0, 0, 0, 0, 0, 0, V3.New())
    end

    Script.Yield(500)
    deleteEntity(randomPed)
end)

