local currentEngineSound = {}
local currentVehicle = nil
Script.RegisterLooped(function()
	local vehicle = getUserVehicle()
	if vehicle ~= currentVehicle then
		currentVehicle = vehicle
		currentEngineSound = {}
		selectedVehicle = nil
	end

	if selectedVehicle ~= nil and currentVehicle ~= nil and not currentEngineSound[selectedVehicle] then
		if vehicle == 0 then
			GUI.AddToast("JinxScript", "You are not in any vehicle. :/", 5000)
			selectedVehicle = nil
			return
		end
		AUDIO.PRELOAD_VEHICLE_AUDIO_BANK(Utils.Joaat(selectedVehicle))
		AUDIO.FORCE_USE_AUDIO_GAME_OBJECT(getUserVehicle(), selectedVehicle)
		Script.Yield()
		AUDIO.SET_VEH_RADIO_STATION(vehicle, "OFF")
		currentEngineSound = {}
		currentEngineSound[selectedVehicle] = true
	end
	Script.Yield(50)
end)

